import './App.css';
import React, { Component } from 'react';

import HomePage from './components/HomePage';
import ThreeDayForecast from './components/ThreeDayForecast'
import HourlyForecast from './components/HourlyForecast';

import { Nav, Navbar } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css'

import { BrowserRouter, Routes, Route, Link } from "react-router-dom";

class App extends Component {
  constructor() {
    super();
  this.state = {
  }

  this.changeCity = this.changeCity.bind(this)
}

  changeCity(input){
    this.setState({ cityName: input }, () => 
      console.log("State: " + this.state.cityName)
  );
  }

  render() {
    return (
    <>
      <BrowserRouter>
      <div>
        <Link to="/">Home</Link>
        <Link to="/counter">Counter</Link>
      </div>
      
      {/* <Navbar className="navtop" bg="dark" variant="dark">
        <Navbar.Brand className="title" href="/home">Weather App</Navbar.Brand>
        <Nav className="me-auto">
          <Nav.Link className="menu" href="/home">Home</Nav.Link>
          <Nav.Link className="menu" href="/3dforecast">3 Day Forecast</Nav.Link>
          <Nav.Link className="menu" href="/hourlyforecast">Hourly Forecast</Nav.Link>
        </Nav>
      </Navbar> */}
      
      
      <Routes>
        <Route path='/home'><div className='app'><HomePage cityInput = {this.state.cityName}/></div></Route>
        <Route path='/3dforecast' children = {({cityName}) => <ThreeDayForecast cityInput = {cityName} changeCity={this.changeCity}/>}/>
        <Route path='/hourlyforecast' children = {({cityName}) => <HourlyForecast cityInput = {cityName} changeCity={this.changeCity}/>}/>
      </Routes>
      </BrowserRouter>
      </>

    );
  }
}

export default App;
