import './App.css';
import React, { Component } from 'react';

import HomePage from './components/HomePage';
import ThreeDayForecast from './components/ThreeDayForecast'
import HourlyForecast from './components/HourlyForecast';

import { Nav, Navbar } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css'

import { BrowserRouter, Link, Route } from "react-router-dom";

class App extends Component {
  constructor() {
    super();
  this.state = {
  }

  this.changeCity = this.changeCity.bind(this)
}

  changeCity(input){
    this.setState({ cityName: input }, () => 
      console.log("State: " + this.state.cityName)
  );
  }

  render() {
    return (
    <>
      <BrowserRouter>
      {/* <div>
        <Link to="/home">Home</Link> <br></br>
        <Link to="/3d">3D</Link><br></br>
        <Link to="/hour">Hourly</Link><br></br>
      </div> */}
      
      <Navbar className="navtop" bg="dark" variant="dark">
        <Navbar.Brand className="title" href="/home">Weather App</Navbar.Brand>
        <Nav className="me-auto">
          <Nav.Link className="menu" href="/home">Home</Nav.Link>
          <Nav.Link className="menu" href="/3dforecast">3 Day Forecast</Nav.Link>
          <Nav.Link className="menu" href="/hourlyforecast">Hourly Forecast</Nav.Link>
        </Nav>
      </Navbar>
      
      
      {/* <Routes> */}
        <Route exact path='/home'><div className='app'><HomePage cityInput = {this.state.cityName}/></div></Route>
        <Route exact path='/3d' children = {({cityName}) => <ThreeDayForecast cityInput = {cityName} changeCity={this.changeCity}/>}/>

        <Route exact path='/hour' children = {({cityName}) => <HourlyForecast cityInput = {cityName} changeCity={this.changeCity}/>}/>
      {/* </Routes> */}
      </BrowserRouter>
      </>

    );
  }
}

export default App;
