//         setHumidity(response.data.humidity + '%')
//         setWind(response.data.wind + ' km/h')
//         setCountry(response.data.countryName)

//     }).catch((error) => {
//         console.error(error)
//     })
// }
async forecastAPI() {
	const options = {
		method: 'GET',
		url: 'http://localhost:3001/cityAPI', //Backend Pull
		params: { city: this.state.name },
	}

	let data = await axios.request(options)
	.then(function (response) {
		return response.data;
	})
	.catch(function (error) {
		console.log(error);
	});

    this.setState({city: data.cityName});
    this.setState({temp: data.currentTemp});
    this.setState({cond: data.condition});
    this.setState({icon: data.icon});
    this.setState({feelsLike: data.feelslike});
    this.setState({humidity: data.humidity});
    this.setState({wind: data.wind});
    this.setState({country: data.countryName});
}

handleSubmit = (e) => {

	e.preventDefault();
	this.forecastAPI();
	this.handleInputChange(this.state.cityName);

}

everyChange = (e) => {

	this.setState({ cityName: e.target.value });
}



