import React, { Component } from "react";
import axios from "axios";

class ForecastAPI extends Component {
constructor() {
	super();
	this.state = {
	};
}

async newAPI() {
	const options = {
		method: 'GET',
		url: 'http://localhost:3001/forecastAPI', //Backend Pull
		params: { city: this.state.cityName },
	}

	let data = await axios.request(options)
	.then(function (response) {
		return response.data;
	})
	.catch(function (error) {
		console.log(error);
	});
	this.setState({ test: data.forecast.forecastday });
}

handleSubmit = (e) => {

	e.preventDefault();
	this.newAPI();

}

everyChange = (e) => {

	this.setState({ cityName: e.target.value });
}


render() {
	const { test } = this.state;
	return (
	<>
		<form onSubmit={this.handleSubmit}>
		<input onChange={this.everyChange} placeholder="Enter Location"></input>
		</form>

		<div>
		{test && test.map(todo => {
			return (
				<>
				<p> Average Temperature: {todo.day.avgtemp_c}</p>
				<div class="card" style="width: 18rem;">
					<img class="card-img-top" src="..." alt="Card image cap"></img>
					<div class="card-body">
						<h5 class="card-title">Card title</h5>
						<p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
						<a href="#" class="btn btn-primary">Go somewhere</a>
					</div>
				</div>
				</>



			);
		})}
		</div>

	</>
	);
}
}

export default ForecastAPI;
