import React, { Component } from "react";
import axios from "axios";

class Usingdidmount extends Component {
  constructor() {
    super();
    this.state = {
      name: "React",
    };
    // this.getTodos = this.getTodos.bind(this);
  }

  componentDidMount() {
    this.getTodos();
    this.newAPI();
  }

  async getTodos() {
    let data = await axios
      .get("https://jsonplaceholder.typicode.com/todos?_limit=10")
      .then(function(response) {
        return response.data;
      })
      .catch(function(error) {
        console.log(error);
      });
    this.setState({ todos: data });
  }

  async newAPI() {
    let data = await axios
      .get("http://api.weatherapi.com/v1/forecast.json?key=d5ccf290643547b2aa3190009220604&q=London&days=7&aqi=no&alerts=no")
      .then(function(response) {
        return response.data;
      })
      .catch(function(error) {
        console.log(error);
      });
    this.setState({ test: data.forecast.forecastday });
  }


  render() {
    const { todos } = this.state;
    const { test } = this.state;
    // console.log(todos);
    console.log(test);
    return (
      <div>
        {test && test.map(todo => {
            return (
              <p>{todo.day.avgtemp_c}</p>
              // <table>
              //   <tr>
              //     {/* <td>{todo.id}</td> */}


              //     <td>
              //       <p>{todo.day.avgtemp_c}</p>


              //     </td>
              //   </tr>
              // </table>
            );
          })}
      </div>
    );
  }
}

export default Usingdidmount;
